sys_script_include.script:
var ACLEvaluatorUtil = Class.create();
ACLEvaluatorUtil.prototype = {
    initialize: function() {
    },
	getRoleAclForTable: function(tableName, roleSysId) {
        var result = {
            create: {
                "access": "none",
                "all": "none",
				"fields": {}
            },
            read: {
                "access": "none",
                "all": "none",
				"fields": {}
            },
            write: {
                "access": "none",
                "all": "none",
				"fields": {}
            },
            del: {
                "access": "none",
                "all": "none",
				"fields": {}
            }
        };
	var role_table = new GlideRecord("sys_user_role");
		var isRoleExist = role_table.get(roleSysId);
		gs.info(isRoleExist);
		if(isRoleExist == false){
			return result;
		}
        // ---------------- WITH ROLE ----------------
        var gr = new GlideRecord("sys_security_acl_role");
        var query = "sys_security_acl.name=" + tableName +
            "^ORsys_security_acl.nameSTARTSWITH" + tableName + "." +
            "^sys_user_role=" + roleSysId;
        gr.addEncodedQuery(query);
        gr.query();
        while (gr.next()) {
            var op = gr.sys_security_acl.operation.toString();
            var acl_name = gr.sys_security_acl.name.toString();
            var parts = acl_name.split(".");
            var field = parts.length > 1 ? parts[1] : "";

            if (op == "create") {
                if (acl_name == tableName) result.create.access = "true";
                else if (field == "*") result.create.all = "true";
                else result.create.fields[field] = "true";
            }

            if (op == "read") {
                if (acl_name == tableName) result.read.access = "true";
                else if (field == "*") result.read.all = "true";
                else result.read.fields[field] = "true";
            }

            if (op == "write") {
                if (acl_name == tableName) result.write.access = "true";
                else if (field == "*") result.write.all = "true";
                else result.write.fields[field] = "true";
            }

            if (op == "delete") {
                if (acl_name == tableName) result.del.access = "true";
                else if (field == "*") result.del.all = "true";
                else result.del.fields[field] = "true";
            }
        }

        // ---------------- WITHOUT ROLE ----------------

        var negation_gr = new GlideRecord("sys_security_acl_role");
        var negation_query = "sys_security_acl.name=" + tableName +
            "^ORsys_security_acl.nameSTARTSWITH" + tableName + "." +
            "^sys_user_role!=" + roleSysId;
        negation_gr.addEncodedQuery(negation_query);
        negation_gr.query();

        while (negation_gr.next()) {
            var op = negation_gr.sys_security_acl.operation.toString();
            var acl_name = negation_gr.sys_security_acl.name.toString();
            var parts = acl_name.split(".");
            var field = parts.length > 1 ? parts[1] : "";

            if (op == "create") {
                if (acl_name == tableName && result.create.access == "none") result.create.access = "false";
                else if (field == "*" && result.create.all == "none") result.create.all = "false";
                else if (!result.create.fields[field]) result.create.fields[field] = "false";
            }

            if (op == "read") {
                if (acl_name == tableName && result.read.access == "none") result.read.access = "false";
                else if (field == "*" && result.read.all == "none") result.read.all = "false";
                else if (!result.read.fields[field]) result.read.fields[field] = "false";
            }

            if (op == "write") {
                if (acl_name == tableName && result.write.access == "none") result.write.access = "false";
                else if (field == "*" && result.write.all == "none") result.write.all = "false";
                else if (!result.write.fields[field]) result.write.fields[field] = "false";
            }

            if (op == "delete") {
                if (acl_name == tableName && result.del.access == "none") result.del.access = "false";
                else if (field == "*" && result.del.all == "none") result.del.all = "false";
                else if (!result.del.fields[field]) result.del.fields[field] = "false";
            }
        }

        return result;
    },

    type: 'ACLEvaluatorUtil'
};
